import React from 'react'
import Logo from '../../assets/black-logo.png'
import { useState } from 'react'

import AOS from 'aos'
import 'aos/dist/aos.css'
import { useEffect } from 'react'



const Nav = () => {

    const isSmallDevice = window.innerWidth <= 768;

    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);

        if (!isMenuOpen) {
            document.body.classList.add('no-scroll');
        } else {
            document.body.classList.remove('no-scroll');
        }
    };

    const [prevScrollPos, setPrevScrollPos] = useState(window.pageYOffset);
    const [isNavbarVisible, setIsNavbarVisible] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
        const currentScrollPos = window.pageYOffset;

        if (currentScrollPos > prevScrollPos) {
            // Scrolling down
            if (currentScrollPos > 200) {
            setIsNavbarVisible(true);
            }
        } else {
            // Scrolling up
            setIsNavbarVisible(false);
        }

        setPrevScrollPos(currentScrollPos);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [prevScrollPos]);

    useEffect(() => {
        AOS.init({duration:1800})
      }, []);

    

    const handleNavItemClick = () => {
        setIsMenuOpen(false);
        document.body.classList.remove('no-scroll');
    };

    

    return (
        <nav className={`navbar ${isNavbarVisible ? 'sticky' : ''}`}>
            <div className="header__nav-content">
                <div className="nav__logo" data-aos='zoom-in'>
                    <a href="#header">
                        <img src={Logo} alt="logo" />
                    </a>
                </div>
                <ul className={`header__nav-menu ${isMenuOpen ? 'show' : ''}`}>
                    <li className={`header__nav-item ${isSmallDevice ? '' : 'aos-init'}`}
                        data-aos='zoom-in' 
                        data-aos-delay='100'>
                        <a href="#" onClick={() => setActiveHeadNav('#')} className={activeHeadNav === '#' ? 'active' : ''}>Home</a> 
                    </li>
                    <li className={`header__nav-item ${isSmallDevice ? '' : 'aos-init'}`}
                        data-aos='zoom-in' 
                        data-aos-delay='200'>
                        <a href="#about" onClick={() => setActiveHeadNav('#about')} className={activeHeadNav === '#about' ? 'active' : ''}>About</a> 
                    </li>
                    <li className={`header__nav-item ${isSmallDevice ? '' : 'aos-init'}`}
                        data-aos='zoom-in' 
                        data-aos-delay='300'>
                        <a href="#projects" onClick={() => setActiveHeadNav('#projects')} className={activeHeadNav === '#projects' ? 'active' : ''}>Projects</a> 
                    </li>
                    <li className={`header__nav-item ${isSmallDevice ? '' : 'aos-init'}`}
                        data-aos='zoom-in' 
                        data-aos-delay='400'>
                        <a href="#contact" onClick={() => setActiveHeadNav('#contact')} className={activeHeadNav === '#contact' ? 'active' : ''}>Contact</a> 
                    </li>
                </ul>

                <div className={`hamburger ${isMenuOpen ? 'show' : ''}`} onClick={toggleMenu}>
                    <span className='bar'></span>
                    <span className='bar'></span>
                    <span className='bar'></span>
                </div>
            </div>
        </nav>
    )
}

export default Nav
