import React from 'react'
import Logo from '../../assets/black-logo.png'
import { useState } from 'react'

import AOS from 'aos'
import 'aos/dist/aos.css'
import { useEffect } from 'react'



const Nav = () => {

    const [prevScrollPos, setPrevScrollPos] = useState(window.pageYOffset);
    const [isNavbarVisible, setIsNavbarVisible] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
        const currentScrollPos = window.pageYOffset;

        if (currentScrollPos > prevScrollPos) {
            // Scrolling down
            if (currentScrollPos > 200) {
            setIsNavbarVisible(true);
            }
        } else {
            // Scrolling up
            setIsNavbarVisible(false);
        }

        setPrevScrollPos(currentScrollPos);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [prevScrollPos]);

    useEffect(() => {
        AOS.init({duration:1800})
      }, []);

    const [activeHeadNav, setActiveHeadNav] = useState('#')

  return (
    <nav className={`navbar ${isNavbarVisible ? 'sticky' : ''}`}>
        <div className="header__nav-content">
            <a href="#header">
                <img src={Logo} alt="logo" data-aos='zoom-in'/>
            </a>
            <ul className="header__nav-menu">
                <li className="header__nav-item" data-aos='zoom-in' data-aos-delay='100'>
                    <a href="#" onClick={() => setActiveHeadNav('#')} className={activeHeadNav === '#' ? 'active' : ''}>Home</a> 
                </li>
                <li className="header__nav-item" data-aos='zoom-in' data-aos-delay='200'>
                    <a href="#about" onClick={() => setActiveHeadNav('#about')} className={activeHeadNav === '#about' ? 'active' : ''}>About</a> 
                </li>
                <li className="header__nav-item" data-aos='zoom-in' data-aos-delay='300'>
                    <a href="#projects" onClick={() => setActiveHeadNav('#projects')} className={activeHeadNav === '#projects' ? 'active' : ''}>Projects</a> 
                </li>
                <li className="header__nav-item" data-aos='zoom-in' data-aos-delay='400'>
                    <a href="#contact" onClick={() => setActiveHeadNav('#contact')} className={activeHeadNav === '#contact' ? 'active' : ''}>Contact</a> 
                </li>
            </ul>

            <div className='hamburger'>
                <span className='bar'></span>
                <span className='bar'></span>
                <span className='bar'></span>
            </div>
        </div>
    </nav>
  )
}

export default Nav
